SPECIAL_CHARS = "[(&Agrave)(&Aacute)(&Acirc)(&Atilde)(&Auml)(&Aring)(&agrave)(&aacute)(&acirc)(&atilde)(&auml)(&aring)(&AElig)(&aelig)(&szlig)(&Ccedil)(&ccedil)(&Egrave)(&Eacute)(&Ecirc)(&Euml)(&egrave)(&eacute)(&ecirc)(&euml)(&#131)(&Igrave)(&Iacute)(&Icirc)(&Iuml)(&igrave)(&iacute)(&icirc)(&iuml)(&Ntilde)(&ntilde)(&Ograve)(&Oacute)(&Ocirc)(&Otilde)(&Ouml)(&ograve)(&oacute)(&ocirc)(&otilde)(&ouml)(&Oslash)(&oslash)(&#140)(&#156)(&#138)(&#154)(&Ugrave)(&Uacute)(&Ucirc)(&Uuml)(&ugrave)(&uacute)(&ucirc)(&uuml)(&#181)(&#215)(&Yacute)(&#159)(&yacute)(&yuml)]"



VALIDATION_PATTERNS = {
    "user_ID": r'\d{2,5}',
    "name": fr"^[a-zA-Z&'\s\.,\-]+$",
    "username": r"^(?=.{3,20}$)(?![-_.])(?!.*[-_.]{2})[a-zA-Z0-9._-]+(?<![-_.])$",
    # "usermail": RFC_EMAIL_VALIDATOR,
    "usermail": r'\s?[\w\d\._\-]+@([a-zA-Z0-9-]+\.)+[a-zA-Z]{2,}\s?',
    "tel": r'\d?-?\(?\d{3}\)?[-.\s]?\d{3}[-.\s]?\d{4}',
    "sex": r'(M|F)',
    "country": r"[\w\s]+",
    "birth": r"\d{4}",
    "user_additional_info": r'.+',
}


class DataInterpreter:
    def __init__(self):
        self.__result_dict = [("user_ID", "name", "username", "usermail", "user_additional_info")]
        self.__not_valid_row = []
        self.__not_valid_data = []

    def add_data(self, data):
        if re.match(pattern=r"VALUES", string=str(data)):
            all_matches = re.findall(r"\(\d{2,5},\s.+\)", f"{str(data)}"[7:], re.IGNORECASE)
            for match in all_matches:
                self.add_row(match.split(",\t"))

    def add_row(self, row):
        print(row)
        self.__not_valid_row = []
        #       0       1           2           3        4           5           err       6         7       8
        #  ("user_ID", "name", "username", "password", "usermail", "permission", "tel",  "sex", "country", "birth")
        self.__result_dict.append((self.validate(value=row[0][1:], kay="user_ID"),
                                   self.validate(value=row[1][1:-1], kay="name"),
                                   self.validate(value=row[2][1:-1], kay="username"),
                                   self.validate(value=row[4][1:-1], kay="usermail"),
                                   self.build_user_additional_info({
                                       "sex": row[6][1:-1],
                                       "country": row[7][1:-1],
                                       "birth": row[len(row) - 1][:-1]})
                                   ))

        # self.__not_valid_row.append(row[3])  # OSINT does not involve passwords
        # self.__not_valid_row.append(row[5])  # permission not have useful information
        if self.__not_valid_row:
            self.__not_valid_data.append(tuple(self.__not_valid_row))

    def validate(self, value, kay):
        """Return valid values or empty string"""
        if re.match(pattern=VALIDATION_PATTERNS.get(kay), string=str(value)):
            return value
        else:
            if value != 0 and value != "0" and value != "":
                self.__not_valid_row.append(value)
            return ""

    def build_user_additional_info(self, dict_of_add_infos):
        result = []
        for add_info in dict_of_add_infos:
            validated_value = self.validate(kay=add_info, value=dict_of_add_infos[add_info])
            if validated_value != "":
                result.append(validated_value)

        return "|".join(result)

    @property
    def get_final_values(self):
        return self.__result_dict

    @property
    def get_not_valid_data(self):
        return self.__not_valid_data
